(function() {
  var code, main, narcissus, param;

  narcissus = require('./src/documents/lib/narcissus_packed');

  code = "foo = 'bar';";

  main = function(scope) {
    var ast;
    ast = narcissus.parser.parse(code);
    console.log(">>>>>> children(" + ast.children.length + "): ", ast.children);
    return console.log(">>>>>> child 0 expression: ", ast.children[0].expression);
  };

  if (require.main === module) {
    param = process.argv[2];
    if (param === 'scope') {
      main(true);
    } else {
      main(false);
    }
  }

}).call(this);
